import React, { useState, useEffect } from 'react';
import EmployeeAssignment from './EmployeeAssignment';
import TimesheetMatrix from './TimesheetMatrix';
import ProjectExpenseSummary from './ProjectExpenseSummary';
import './CreateProject.css';

const CreateProject = ({ onProjectSaved, onCancel, initialData, isUpdateMode = false }) => {
    const [formData, setFormData] = useState(initialData ? {
        startDate: initialData.startDate || '',
        endDate: initialData.endDate || '',
        projectType: initialData.projectType || '',
        projectDescription: initialData.projectDescription || '',
        projectBudget: initialData.projectBudget ? initialData.projectBudget.toString() : '',
        perDayRate: initialData.perDayRate ? initialData.perDayRate.toString() : '',
        perHourRate: initialData.perHourRate ? initialData.perHourRate.toString() : ''
    } : {
        startDate: '',
        endDate: '',
        projectType: '',
        projectDescription: '',
        projectBudget: '',
        perDayRate: '',
        perHourRate: ''
    });

    const [currentSection, setCurrentSection] = useState(1);
    const [projectId, setProjectId] = useState(initialData ? initialData.id : null);
    const [assignedEmployees, setAssignedEmployees] = useState([]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const validateSection = (section) => {
        switch (section) {
            case 1:
                return formData.startDate && formData.endDate && formData.projectType;
            case 2:
                return formData.projectBudget || formData.perDayRate || formData.perHourRate;
            default:
                return true;
        }
    };

    const handleSaveProject = async () => {
        try {
            const projectData = {
                ...formData,
                projectBudget: formData.projectBudget ? parseFloat(formData.projectBudget) : null,
                perDayRate: formData.perDayRate ? parseFloat(formData.perDayRate) : null,
                perHourRate: formData.perHourRate ? parseFloat(formData.perHourRate) : null
            };

            const url = isUpdateMode ? `/api/projects/${projectId}` : '/api/projects';
            const method = isUpdateMode ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include',
                body: JSON.stringify(projectData)
            });

            if (response.ok) {
                const savedProject = await response.json();
                if (!isUpdateMode) {
                    setProjectId(savedProject.id);
                }
                setCurrentSection(3);
                return savedProject;
            } else {
                alert(`Failed to ${isUpdateMode ? 'update' : 'save'} project`);
                return null;
            }
        } catch (error) {
            console.error(`Error ${isUpdateMode ? 'updating' : 'saving'} project:`, error);
            alert(`Error ${isUpdateMode ? 'updating' : 'saving'} project`);
            return null;
        }
    };

    const handleNext = () => {
        if (validateSection(currentSection)) {
            if (currentSection === 2) {
                handleSaveProject();
            } else {
                setCurrentSection(currentSection + 1);
            }
        } else {
            alert('Please fill in all required fields');
        }
    };

    const handlePrevious = () => {
        setCurrentSection(currentSection - 1);
    };

    const handleEmployeesAssigned = (employees) => {
        setAssignedEmployees(employees);
        setCurrentSection(4);
    };

    const handleFinish = () => {
        onProjectSaved();
    };

    const renderSection1 = () => (
        <div className="form-section">
            <h3>Section 1: Project Details</h3>
            <div className="form-row">
                <div className="form-group">
                    <label>Start Date *</label>
                    <input
                        type="date"
                        name="startDate"
                        value={formData.startDate}
                        onChange={handleInputChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>End Date *</label>
                    <input
                        type="date"
                        name="endDate"
                        value={formData.endDate}
                        onChange={handleInputChange}
                        min={formData.startDate}
                        required
                    />
                </div>
            </div>
            <div className="form-row">
                <div className="form-group">
                    <label>Project Type *</label>
                    <select
                        name="projectType"
                        value={formData.projectType}
                        onChange={handleInputChange}
                        required
                    >
                        <option value="">Select Project Type</option>
                        <option value="Development">Development</option>
                        <option value="Consulting">Consulting</option>
                        <option value="Maintenance">Maintenance</option>
                        <option value="Research">Research</option>
                        <option value="Testing">Testing</option>
                        <option value="Other">Other</option>
                    </select>
                </div>
            </div>
            <div className="form-group">
                <label>Project Description</label>
                <textarea
                    name="projectDescription"
                    value={formData.projectDescription}
                    onChange={handleInputChange}
                    placeholder="Enter project description"
                    rows="4"
                />
            </div>
        </div>
    );

    const renderSection2 = () => (
        <div className="form-section">
            <h3>Section 2: Budget Information</h3>
            <div className="form-row">
                <div className="form-group">
                    <label>Project Budget</label>
                    <input
                        type="number"
                        step="0.01"
                        name="projectBudget"
                        value={formData.projectBudget}
                        onChange={handleInputChange}
                        placeholder="Enter total project budget"
                    />
                </div>
            </div>
            <div className="form-row">
                <div className="form-group">
                    <label>Per Day Rate</label>
                    <input
                        type="number"
                        step="0.01"
                        name="perDayRate"
                        value={formData.perDayRate}
                        onChange={handleInputChange}
                        placeholder="Rate per day"
                    />
                </div>
                <div className="form-group">
                    <label>Per Hour Rate</label>
                    <input
                        type="number"
                        step="0.01"
                        name="perHourRate"
                        value={formData.perHourRate}
                        onChange={handleInputChange}
                        placeholder="Rate per hour"
                    />
                </div>
            </div>
            <div className="rate-note">
                <p><small>* At least one rate field (Budget, Per Day Rate, or Per Hour Rate) is required</small></p>
            </div>
        </div>
    );

    const renderSection3 = () => (
        <div className="form-section">
            <h3>Section 3: Assign Resources</h3>
            {projectId && (
                <EmployeeAssignment
                    projectId={projectId}
                    onEmployeesAssigned={handleEmployeesAssigned}
                />
            )}
        </div>
    );

    const renderSection4 = () => (
        <div className="form-section">
            <h3>Section 4: Timesheet & Expense Summary</h3>
            {projectId && (
                <>
                    <TimesheetMatrix
                        projectId={projectId}
                        startDate={formData.startDate}
                        endDate={formData.endDate}
                        assignedEmployees={assignedEmployees}
                    />
                    <ProjectExpenseSummary projectId={projectId} />
                </>
            )}
        </div>
    );

    const renderNavigationButtons = () => (
        <div className="navigation-buttons">
            {currentSection > 1 && (
                <button type="button" onClick={handlePrevious} className="btn-secondary">
                    Previous
                </button>
            )}
            {currentSection < 4 ? (
                <button type="button" onClick={handleNext} className="btn-primary">
                    {currentSection === 2 ? (isUpdateMode ? 'Update & Continue' : 'Save & Continue') : 'Next'}
                </button>
            ) : (
                <button type="button" onClick={handleFinish} className="btn-success">
                    Finish
                </button>
            )}
            <button type="button" onClick={onCancel} className="btn-cancel">
                Cancel
            </button>
        </div>
    );

    return (
        <div className="create-project">
            <div className="form-header">
                <h2>{isUpdateMode ? 'Update Project' : 'Create New Project'}</h2>
                <div className="section-indicators">
                    {[1, 2, 3, 4].map(num => (
                        <div 
                            key={num} 
                            className={`section-indicator ${num === currentSection ? 'active' : ''} ${num < currentSection ? 'completed' : ''}`}
                        >
                            {num}
                        </div>
                    ))}
                </div>
            </div>

            <form onSubmit={(e) => e.preventDefault()}>
                {currentSection === 1 && renderSection1()}
                {currentSection === 2 && renderSection2()}
                {currentSection === 3 && renderSection3()}
                {currentSection === 4 && renderSection4()}

                {renderNavigationButtons()}
            </form>
        </div>
    );
};

export default CreateProject;
