import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ApprovalPending = () => {
  const [pendingRequests, setPendingRequests] = useState([]);
  const [pendingInventoryRequests, setPendingInventoryRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showRejectModal, setShowRejectModal] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [rejectionReason, setRejectionReason] = useState('');
  const [activeTab, setActiveTab] = useState('employee');

  useEffect(() => {
    fetchPendingRequests();
  }, []);

  const fetchPendingRequests = async () => {
    try {
      setLoading(true);
      const [employeeResponse, inventoryResponse] = await Promise.all([
        axios.get('/api/employee-requests/pending', { withCredentials: true }),
        axios.get('/api/employee-requests/inventory/pending', { withCredentials: true })
      ]);
      setPendingRequests(employeeResponse.data);
      setPendingInventoryRequests(inventoryResponse.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch pending requests');
      console.error('Error fetching pending requests:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (requestId, isInventory = false) => {
    const requestType = isInventory ? 'inventory' : 'employee';
    if (window.confirm(`Are you sure you want to approve this ${requestType} request?`)) {
      try {
        const endpoint = isInventory 
          ? `/api/employee-requests/inventory/approve/${requestId}`
          : `/api/employee-requests/approve/${requestId}`;

        await axios.post(endpoint, {}, {
          withCredentials: true
        });
        alert(`${requestType.charAt(0).toUpperCase() + requestType.slice(1)} request approved successfully`);
        fetchPendingRequests();
      } catch (err) {
        alert('Failed to approve request: ' + (err.response?.data?.error || err.message));
      }
    }
  };

  const handleRejectClick = (request, isInventory = false) => {
    setSelectedRequest({...request, isInventory});
    setShowRejectModal(true);
    setRejectionReason('');
  };

  const handleReject = async () => {
    if (!rejectionReason.trim()) {
      alert('Please provide a rejection reason');
      return;
    }

    try {
      const endpoint = selectedRequest.isInventory 
        ? `/api/employee-requests/inventory/reject/${selectedRequest.id}`
        : `/api/employee-requests/reject/${selectedRequest.id}`;

      await axios.post(endpoint, {
        reason: rejectionReason
      }, {
        withCredentials: true
      });

      const requestType = selectedRequest.isInventory ? 'Inventory' : 'Employee';
      alert(`${requestType} request rejected`);
      setShowRejectModal(false);
      fetchPendingRequests();
    } catch (err) {
      alert('Failed to reject request: ' + (err.response?.data?.error || err.message));
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="approval-pending">
      <h2>Pending Requests</h2>

      <div style={{ marginBottom: '20px' }}>
        <button 
          onClick={() => setActiveTab('employee')}
          style={{
            padding: '10px 20px',
            marginRight: '10px',
            backgroundColor: activeTab === 'employee' ? '#007bff' : '#f8f9fa',
            color: activeTab === 'employee' ? 'white' : '#333',
            border: '1px solid #007bff',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Employee Requests ({pendingRequests.length})
        </button>
        <button 
          onClick={() => setActiveTab('inventory')}
          style={{
            padding: '10px 20px',
            backgroundColor: activeTab === 'inventory' ? '#007bff' : '#f8f9fa',
            color: activeTab === 'inventory' ? 'white' : '#333',
            border: '1px solid #007bff',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Inventory Requests ({pendingInventoryRequests.length})
        </button>
      </div>

      {activeTab === 'employee' && (
        <>
          {pendingRequests.length === 0 ? (
            <p>No pending employee requests</p>
          ) : (
            <div className="table-container">
              <table className="data-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Employee ID</th>
                    <th>Passport ID</th>
                    <th>Emirates ID</th>
                    <th>Phone</th>
                    <th>Joining Date</th>
                    <th>Salary</th>
                    <th>Requested By</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {pendingRequests.map((request) => (
                    <tr key={request.id}>
                      <td>{request.name}</td>
                      <td>{request.empId}</td>
                      <td>{request.passportId}</td>
                      <td>{request.emiratesId}</td>
                      <td>{request.phoneNumber}</td>
                      <td>{new Date(request.joiningDate).toLocaleDateString()}</td>
                      <td>${request.salary}</td>
                      <td>{request.requestedBy?.username}</td>
                      <td>
                        <button 
                          className="btn btn-approve"
                          onClick={() => handleApprove(request.id, false)}
                        >
                          Approve
                        </button>
                        <button 
                          className="btn btn-reject"
                          onClick={() => handleRejectClick(request, false)}
                        >
                          Reject
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </>
      )}

      {activeTab === 'inventory' && (
        <>
          {pendingInventoryRequests.length === 0 ? (
            <p>No pending inventory requests</p>
          ) : (
            <div className="table-container">
              <table className="data-table">
                <thead>
                  <tr>
                    <th>Type</th>
                    <th>Inventory ID</th>
                    <th>Name</th>
                    <th>Quantity</th>
                    <th>Price/Unit</th>
                    <th>Total Price</th>
                    <th>Supplier</th>
                    <th>Requested By</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {pendingInventoryRequests.map((request) => (
                    <tr key={request.id}>
                      <td>{request.requestType}</td>
                      <td>{request.inventoryId}</td>
                      <td>{request.name}</td>
                      <td>{request.quantity}</td>
                      <td>${request.perQuantityPrice}</td>
                      <td>${request.totalPrice}</td>
                      <td>{request.supplierName}</td>
                      <td>{request.requestedBy}</td>
                      <td>
                        <button 
                          className="btn btn-approve"
                          onClick={() => handleApprove(request.id, true)}
                        >
                          Approve
                        </button>
                        <button 
                          className="btn btn-reject"
                          onClick={() => handleRejectClick(request, true)}
                        >
                          Reject
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </>
      )}

      {showRejectModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Reject {selectedRequest?.isInventory ? 'Inventory' : 'Employee'} Request</h3>
            <p>{selectedRequest?.isInventory ? 'Inventory' : 'Employee'}: {selectedRequest?.name}</p>
            <textarea
              placeholder="Enter rejection reason..."
              value={rejectionReason}
              onChange={(e) => setRejectionReason(e.target.value)}
              rows="4"
              style={{ width: '100%' }}
            />
            <div className="modal-actions">
              <button className="btn btn-secondary" onClick={() => setShowRejectModal(false)}>
                Cancel
              </button>
              <button className="btn btn-danger" onClick={handleReject}>
                Reject Request
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ApprovalPending;
